CREATE TABLE IF NOT EXISTS bep20.tokens (
	contract_address bytea UNIQUE,
	symbol text,
	decimals integer
);

BEGIN;
DELETE FROM bep20.tokens *;



COMMIT;

CREATE INDEX IF NOT EXISTS bep20_tokens_contract_address_decimals_idx ON bep20.tokens USING btree (contract_address) INCLUDE (decimals);
CREATE INDEX IF NOT EXISTS bep20_tokens_symbol_decimals_idx ON bep20.tokens USING btree (symbol) INCLUDE (decimals);
